sys_script.script:
(function executeRule(current, previous /*null when async*/) {
//This function will be automatically called whtn this rule is processed.
	// Add event when attendee inserted
	if(current.operation() == 'insert' && current.marketing_event.changes()) { 
		//current operation performed and marketing_event field change value 
		gs.eventQueue('x_86090_marketing.attendee.added', current, current.marketing_event, current.email);
		//add an event to the event queue, in this case, add x_86090_marketing_attendee.added event, passes the current object, pass the current value of marketing_event and email field
	}
	//add event when marketing event changes
	if(current.operation() == 'update' && current.marketing_event.changes()){
		gs.eventQueue('x_86090_marketing.attendee.deleted', previous, previous.marketing_event, previous.email);
		gs.eventQueue('x_86090_marketing.attendee.added', current, current.marketing_event, current.email);
	}
	//Add event when attendee deleted
	if(current.operation() == 'delete'){
		gs.eventQueue('x_86090_marketing.attendee.deleted', current, current.marketing_event, current.email);
	}

})(current, previous);
