sysevent_script_action.script:
(function removeAttendees() {
	//Get marketing event from event
	var mrkevent = new GlideRecord('x_86090_marketing_marketing_event');
	mrkevent.get(event.parm1); 
	//Count attendees for marketing event
	var numOfAttendees = 0;
	var attendees = new GlideAggregate('x_86090_marketing_attendee');
	attendees.addQuery('marketing_event', event.parm1); 
	attendees.addAggregate('COUNT'); //specify 'COUNT' aggregation query
	attendees.query();
	if(attendees.next())
		numOfAttendees = attendees.getAggregate('COUNT'); //retrive the value of 'COUNT' aggregation query
	//Set attendees registered field
	mrkevent.number_of_attendees_registered = numOfAttendees;
	mrkevent.update(); //save any changes made to object
}());
